import { useState, useEffect } from 'react'
import styles from '../../styles/utils.module.css'
import Button from '@material-ui/core/Button'

export const User = ({user}) => {
    const [confirm, setConfirm] = useState(false)
    const [isDeleting,setIsDeleting]= useState(false)
    
    return(
        <>
            {
            isDeleting ? <></>
            :
            <>   
              <div className={styles.userInfo}>
                <b><label>Email</label></b><label>{user.email}</label><br/>
                <label>Full Name</label><label>{user.firstName}&nbsp;{user.lastName}</label><br/>
                <label>Birth Date</label> <label>{user.date}</label><br/>
                <label>City</label><label>{user.city}</label><br/>
                <label>Address</label><label>{user.address}</label><br/>
                <Button >Delete</Button>
              </div>
            </>
            }
        </>
    )
}
export default User;

User.getInitialProps = async ({ query: { id } }) => {
    const res = await fetch(`http://localhost:3000/api/users/${id}`)
    const {data} = await res.json()
    console.log(res)
    return {user: data}
}
