import React from 'react';
import Button from '@material-ui/core/Button'
import AppBar from '@material-ui/core/AppBar';
import TextField from '@material-ui/core/TextField';
import {useState} from 'react'
import { useRouter } from 'next/router'
import styles from '../styles/utils.module.css'
import { makeStyles } from '@material-ui/core/styles'
import { PlayCircleFilledWhite } from '@material-ui/icons';

const useStyles = makeStyles({
  textField: {
    marginTop:7,
    marginBottom:10
  },
  inputRoot: {
    height:2,
  },
  label: {
    marginTop:15,
    fontSize:14,
    fontWeight:600,
  },
  titleLogin: {
    textAlign:'center',
    color:'white',
    fontWeight:400,
    marginTop:50,
    marginBottom:15
  }
});

export const FormLogin = () => {
    const classes = useStyles();

    const [campi, setCampi]= useState({
      email:'',
      password:''
    })

    const router = useRouter()

    const handleChange = (e) =>{
      setCampi({ ...campi, [e.target.name]: e.target.value })
    }

    const handleSubmit = (e)=>{
      e.preventDefault()
      //validateUser()
      router.push("/userLogged")
    }

    const validateUser = () => {
      //
    }

    const { email, password } = campi
    const values = { email, password }

      return(
           <>
              <AppBar title="Enter details" />
              <div className={styles.main} >
              <h2 className={classes.titleLogin}>Sign in to Whoplays</h2>
              <form onSubmit={handleSubmit} style={{ backgroundColor:'#eaecef', padding:30, borderRadius:10}}>
              <label className={classes.label}>Username or email address</label><br/>
              <TextField
                className={classes.textField}
                inputProps={{
                  className: classes.inputRoot
                }}
                required
                variant="outlined"
                name="email"
                type="email"
                value={campi.email}
                onChange={handleChange}
              />
              <br/>
              <label className={classes.label}>Password</label><br/>
              <TextField
                className={classes.textField}
                inputProps={{
                  className: classes.inputRoot
                }}
                required
                variant="outlined"
                name="password"
                type="password"
                value={campi.password}
                onChange={handleChange}
              />
              <br/><br/>
              <Button
              fullWidth
                type="submit"
                style={_styles.button}
                label="Log in"
              >Log in</Button>
              </form>
            </div>
          </>
    );
    
}
const _styles={
    button:{
        
        fontSize:'13px',
        backgroundColor: '#2ea44f'
    },
}
export default FormLogin;
